example.code:
new LightGbmRegressor
{
  NumBoostRound = 100,
  LearningRate = 0.5f,
  NumLeaves = 32,
  MinDataPerLeaf = 20,
  Booster = new DartBoosterParameterFunction
  {
    XgboostDartMode = true,
    UniformDrop = true
  }
}

new LightGbmRanker
{
  NumBoostRound = 100,
  LearningRate = 0.5f,
  NumLeaves = 32,
  MinDataPerLeaf = 20,
  Booster = new GbdtBoosterParameterFunction
  {
    MinSplitGain = 3,
    MaxDepth = 200,
    Subsample = 0.5
  }
}
