example.code:
new LightGbmRegressor
{
  NumberOfIterations = 100,
  LearningRate = 0.5f,
  NumberOfLeaves = 32,
  MinimumDataPerLeaf = 20,
  Booster = new DartBoosterParameterFunction
  {
    XgboostDartMode = true,
    UniformDrop = true
  }
}

new LightGbmRanker
{
  NumberOfIterations = 100,
  LearningRate = 0.5f,
  NumberOfLeaves = 32,
  MinimumDataPerLeaf = 20,
  Booster = new GbdtBoosterParameterFunction
  {
    MinSplitGain = 3,
    MaxDepth = 200,
    Subsample = 0.5
  }
}
